<div class="row-fluid">    <div class="span12">        <div class="widget blue">            <div class="widget-title">                <h4><i class="icon-reorder"></i> View Customer</h4>                <span class="tools">                <a href="javascript:;" class="icon-chevron-down"></a>                <a href="javascript:;" class="icon-remove"></a>                </span>            </div>            <div class="widget-body">                    <form action="#" class="form-horizontal">                    <label><strong>Customer Photo</strong></label>                     <img src="<?php echo base_url().$customer_info->customer_image;?>" height="200" width="200" alt="customer_images" />                    <br /><br />                    <label><strong>First Name:</strong></label>                     <input type="hidden" class="span6 typeahead" id="typeahead"  value="<?php echo $customer_info->customer_id;?>" >                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_first_name;?>" />                    <br /><br />                    <label><strong>Last Name:</strong></label>                                  <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_last_name;?>" />                    <br /><br />                    <label><strong>User Name:</strong></label>                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_user_name;?>" />                    <br /><br />                    <label><strong>E-Mail Address:</strong></label>                    <input type="email" size="35" class="required email" value="<?php echo $customer_info->customer_email;?>" />                    <br /><br />                    <label><strong>Temporary Address:</strong></label>                    <textarea rows="4" cols="50" autofocus><?php echo $customer_info->customer_address_temporary;?></textarea>                    <br /><br />                    <label><strong>Permanent Address:</strong></label>                    <textarea rows="4" cols="50" autofocus><?php echo $customer_info->customer_address_permanent;?></textarea>                    <br /><br />                    <label><strong>City:</strong></label>                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_city;?>" />                    <br /><br />                    <label><strong>State:</strong></label>                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_state;?>" />                    <br /><br />                    <label><strong>Postal Code:</strong></label>                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_postal_code;?>" />                    <br /><br />                    <label><strong>Country:</strong></label>                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_country;?>" />                    <br /><br />                    <label><strong>Phone:</strong></label>                    <input type="text" size="35" class="required" value="<?php echo $customer_info->customer_phone;?>" />                    <span class="clear border_ddd"></span>                    <br /><br />                </form>            </div>        </div>    </div></div>